parameters.jsCode:
const yesterday = new Date();
yesterday.setDate(yesterday.getDate() - 1);
yesterday.setHours(0, 0, 0, 0);

const today = new Date();
today.setHours(0, 0, 0, 0);

return {
  start_date: yesterday.toISOString(),
  end_date: today.toISOString(),
  report_date: yesterday.toISOString().split('T')[0],
  display_date: yesterday.toLocaleDateString('en-US', { 
    weekday: 'long', 
    year: 'numeric', 
    month: 'long', 
    day: 'numeric' 
  })
};

const transactions = $('Get Yesterday\'s Transactions').all().map(item => item.json);
const findings = $('Get Yesterday\'s Findings').all().map(item => item.json);
const dateInfo = $('Calculate Date Range').item.json;

// Calculate summary metrics
const totalTransactions = transactions.length;
const transactionsWithLeakage = transactions.filter(t => t.has_leakage).length;
const totalLeakage = transactions.reduce((sum, t) => sum + (parseFloat(t.leakage_amount) || 0), 0);

// Group by leakage type
const leakageByType = findings.reduce((acc, f) => {
  const type = f.leakage_type || 'unknown';
  if (!acc[type]) {
    acc[type] = { count: 0, amount: 0 };
  }
  acc[type].count++;
  acc[type].amount += parseFloat(f.leakage_amount) || 0;
  return acc;
}, {});

// Count by severity
const criticalFindings = findings.filter(f => f.severity === 'critical').length;
const highFindings = findings.filter(f => f.severity === 'high').length;
const mediumFindings = findings.filter(f => f.severity === 'medium').length;
const lowFindings = findings.filter(f => f.severity === 'low').length;

// Top sellers with leakage
const sellerLeakage = transactions
  .filter(t => t.has_leakage)
  .reduce((acc, t) => {
    const seller = t.seller_id;
    if (!acc[seller]) {
      acc[seller] = { count: 0, amount: 0 };
    }
    acc[seller].count++;
    acc[seller].amount += parseFloat(t.leakage_amount) || 0;
    return acc;
  }, {});

const topSellers = Object.entries(sellerLeakage)
  .sort((a, b) => b[1].amount - a[1].amount)
  .slice(0, 5)
  .map(([id, data]) => ({ seller_id: id, ...data }));

return {
  report_date: dateInfo.report_date,
  display_date: dateInfo.display_date,
  total_transactions: totalTransactions,
  transactions_with_leakage: transactionsWithLeakage,
  leakage_percentage: totalTransactions > 0 ? ((transactionsWithLeakage / totalTransactions) * 100).toFixed(1) : 0,
  total_leakage_amount: totalLeakage.toFixed(2),
  leakage_by_type: leakageByType,
  critical_findings: criticalFindings,
  high_findings: highFindings,
  medium_findings: mediumFindings,
  low_findings: lowFindings,
  total_findings: findings.length,
  top_sellers: topSellers
};

